function getLinesFromFileContent($content) {\n    // Normalize line endings\n    $content = str_replace(["\r\n", "\r"], "\n", $content);\n    // Explode content by newline\n    $lines = explode("\n", $content);\n    \n    return $lines;\n}\n\n// Example usage\n$fileContent = file_get_contents('path_or_url_to_your_file.txt');\n$lines = getLinesFromFileContent($fileContent);\n\nprint_r($lines);