function getIncompleteClassProperties($object) {\n    if (is_object($object)) {\n        $array = (array) $object;\n        $properties = [];\n        foreach ($array as $key => $value) {\n            // Remove the class name (before the NULL byte) from the key\n            $cleanKey = preg_replace('/^\0.*\0/', '', $key);\n            $properties[$cleanKey] = $value;\n        }\n        return $properties;\n    }\n    return null;\n}\n\n// Usage\n$object = /* your __PHP_Incomplete_Class object */;\n$properties = getIncompleteClassProperties($object);\nvar_dump($properties);