function decryptData($encrypted_data, $encryption_key) {\n    $cipher = "aes-256-cbc"; // Cipher method\n    $encrypted_data = base64_decode($encrypted_data);\n    $ivlen = openssl_cipher_iv_length($cipher);\n    $iv = substr($encrypted_data, 0, $ivlen);\n    $encrypted = substr($encrypted_data, $ivlen);\n    $decrypted = openssl_decrypt($encrypted, $cipher, $encryption_key, 0, $iv);\n    return $decrypted;\n}\n\n$encrypted_data = "encrypted data received from the other server";\n$encryption_key = "your-secret-encryption-key"; // Same key used for encryption\n$decrypted_data = decryptData($encrypted_data, $encryption_key);\n\necho $decrypted_data;