function addChildToParent(SimpleXMLElement $parent, SimpleXMLElement $child) {\n    // Create a new child element under the parent with the same name\n    $newChild = $parent->addChild($child->getName(), (string) $child);\n\n    // Copy attributes\n    foreach ($child->attributes() as $attrName => $attrValue) {\n        $newChild->addAttribute($attrName, $attrValue);\n    }\n\n    // Recursively add child elements\n    foreach ($child->children() as $grandChild) {\n        addChildToParent($newChild, $grandChild);\n    }\n}\n\n// Example usage\n$parent = new SimpleXMLElement('<parent></parent>');\n$child = new SimpleXMLElement('<child><grandchild>value</grandchild></child>');\n\naddChildToParent($parent, $child);\n\necho $parent->asXML();