<?php\n$start = time();\n\nfor ($i = 1010101010; $i < 1389026624; $i += 10) {\n    $square = $i * $i;\n    $squareStr = (string)$square;\n    \n    if ($squareStr[0] == '1' && $squareStr[2] == '2' && $squareStr[4] == '3' &&\n        $squareStr[6] == '4' && $squareStr[8] == '5' && $squareStr[10] == '6' &&\n        $squareStr[12] == '7' && $squareStr[14] == '8' && $squareStr[16] == '9' && $squareStr[18] == '0') {\n        echo $i;\n        break;\n    }\n}\n\n$end = time();\n$elapsed = ($end - $start);\necho "<br />The time to calculate was $elapsed seconds";\n?>