function topPg($currentId) {\n    // Retrieve the current page using the current ID\n    $currentPage = $this->retrieve("id = '$currentId'");\n\n    // Base condition: if the parent is zero, return the current page\n    if ($currentPage->parent == 0) {\n        return $currentPage;\n    } else {\n        // Recursive call: pass the parent ID to the next recursive call\n        return $this->topPg($currentPage->parent);\n    }\n}