$sql = 'SELECT photos.photo_id, photos.photo_title, photos.photo_filename, photos.photographer_id, photographers.photographer_name\n       FROM photos\n       LEFT JOIN photographers ON photos.photographer_id = photographers.photographer_id\n       WHERE photos.photo_id = ?';\n\n//initialize prepared statement\n$stmt = $conn->stmt_init(); \nif ($stmt->prepare($sql)) { \n  $stmt->bind_param('i', $_GET['photo_id']); \n  $stmt->bind_result($photo_id, $photo_title, $photo_filename, $photographer_id, $photographer_name); \n  $OK = $stmt->execute(); \n  $stmt->fetch();\n  \n  // Use the fetched data\n  echo "Photo ID: $photo_id<br>";\n  echo "Photo Title: $photo_title<br>";\n  echo "Photo Filename: $photo_filename<br>";\n  echo "Photographer ID: $photographer_id<br>";\n  echo "Photographer Name: $photographer_name<br>";\n  \n  $stmt->close();\n}