<?php\nfunction generateProfileUrl($id, $name) {\n    $seoName = strtolower(str_replace(' ', '-', $name));\n    return "me.com/profile/$id/$seoName";\n}\n\nfunction generateStoreUrl($id, $name) {\n    $seoName = strtolower(str_replace(' ', '-', $name));\n    return "me.com/store/$id/$seoName";\n}\n\n// Example usage\necho generateProfileUrl(24443, 'Quincy Jones'); // Outputs: me.com/profile/24443/quincy-jones\necho generateStoreUrl(24111, 'Robert Adams'); // Outputs: me.com/store/24111/robert-adams\n?>