$p = new PDFlib();\n\ntry {\n    if ($p->begin_document("large_output.pdf", "optimize=true") == 0) {\n        throw new Exception("Error: " . $p->get_errmsg());\n    }\n\n    $p->set_option("flush=page");\n\n    // Start a new page\n    $p->begin_page_ext(595, 842, "");\n\n    // Add content to the page\n    $font = $p->load_font("Helvetica-Bold", "winansi", "");\n    $p->setfont($font, 24);\n    $p->show_xy("Hello, world!", 50, 700);\n\n    // End the page\n    $p->end_page_ext("");\n\n    // End the document\n    $p->end_document("");\n}\ncatch (Exception $e) {\n    echo $e->getMessage();\n}\nfinally {\n    $p->delete(); // Free up resources\n}