<?php\nfunction getActiveUsers($sessionDir)\n{\n    $users = [];\n    foreach (glob("$sessionDir/sess_*") as $file) {\n        $data = file_get_contents($file);\n        if ($data) {\n            $sessionData = session_decode($data);\n            if (!empty($_SESSION['user_id'])) {\n                $users[$_SESSION['user_id']] = $_SESSION['username'];\n            }\n        }\n    }\n    return $users;\n}\n\n$activeUsers = getActiveUsers(__DIR__ . '/sessions');\nprint_r($activeUsers);\n?>