<?php\nheader('Content-Type: application/json');\n\nif ($_SERVER['REQUEST_METHOD'] === 'POST') {\n    // Assume you have code here to insert the new category into the database\n    // and obtain the ID and NAME of the new category.\n    \n    // Example values\n    $id = 123; // This should be the actual ID from the database\n    $name = $_POST['name']; // This is the name sent from the JS\n\n    // Create an associative array to hold the response data\n    $response = array(\n        'id' => $id,\n        'name' => $name\n    );\n\n    // Encode the array as JSON and return it\n    echo json_encode($response);\n}\n?>