<?php\nfunction getBestPhoto($image_array) {\n    $bestPhoto = null;\n    $priority = [\n        'large' => null,\n        '260' => null,\n        '265' => null,\n        '600' => null,\n        '220' => null\n    ];\n\n    foreach ($image_array as $image) {\n        if (strpos($image, '-large') !== false) {\n            return $image; // Rule 1: If it contains "-large", return it immediately\n        }\n\n        $size = getimagesize($image);\n        $width = $size[0];\n\n        if ($width == 260 && !$priority['260']) {\n            $priority['260'] = $image; // Rule 2: Save 260px wide image if not already found\n        } elseif ($width == 265 && !$priority['265']) {\n            $priority['265'] = $image; // Rule 3: Save 265px wide image if not already found\n        } elseif ($width == 600 && !$priority['600']) {\n            $priority['600'] = $image; // Rule 4: Save 600px wide image if not already found\n        } elseif ($width == 220 && !$priority['220']) {\n            $priority['220'] = $image; // Rule 5: Save 220px wide image if not already found\n        }\n    }\n\n    // Return the best available photo based on criteria\n    foreach ($priority as $photo) {\n        if ($photo) {\n            return $photo;\n        }\n    }\n\n    return null; // No suitable photo found\n}\n\n// Example usage\n$image_array = [\n    'path/to/image1.jpg',\n    'path/to/image2-large.jpg',\n    'path/to/image3.jpg',\n    'path/to/image4.jpg',\n    'path/to/image5.jpg'\n];\n\necho getBestPhoto($image_array);\n?>