<?php\n$url = $_GET['url'];\n$domain = $_GET['domain'];\n\n// Code to log the entry into MySQL database\n// Assuming you have a function logToDatabase($domain, $url) that handles the logging\nlogToDatabase($domain, $url);\n\n// Redirect to the original file\nheader("Location: /$url");\nexit();\n\nfunction logToDatabase($domain, $url) {\n    // Your MySQL logging logic here\n    // Example: Insert into database\n    $mysqli = new mysqli("localhost", "username", "password", "database");\n\n    if ($mysqli->connect_error) {\n        die("Connection failed: " . $mysqli->connect_error);\n    }\n\n    $stmt = $mysqli->prepare("INSERT INTO access_logs (domain, url, access_time) VALUES (?, ?, NOW())");\n    $stmt->bind_param("ss", $domain, $url);\n    $stmt->execute();\n    $stmt->close();\n    $mysqli->close();\n}\n?>