<?php\n\nfunction distributeAmount($amount, $chargeCode) {\n    // Define the regex patterns for the agent types\n    $patterns = [\n        'A' => '/A(?!2)/',  // Matches 'A' but not 'A2'\n        'J' => '/J|A2/',    // Matches 'J' or 'A2'\n        'L' => '/L/',       // Matches 'L'\n    ];\n\n    // Initialize the divide counter and the agent types array\n    $divide = 0;\n    $agentTypes = [];\n\n    // Check for each agent type in the charge code\n    foreach ($patterns as $agent => $pattern) {\n        if (preg_match($pattern, $chargeCode)) {\n            $divide++;\n            $agentTypes[] = $agent;\n        }\n    }\n\n    // Calculate the amount per agent type\n    $amountPerAgent = $divide > 0 ? $amount / $divide : 0;\n\n    // Prepare the result array\n    $result = [];\n    foreach ($agentTypes as $agent) {\n        $result[$agent] = $amountPerAgent;\n    }\n\n    return $result;\n}\n\n// Example usage\n$amount = 50;\n$chargeCode = "AA2";\n\n$result = distributeAmount($amount, $chargeCode);\nprint_r($result);\n?>