$Ar = new Arabic('EnTransliteration');\nwhile ($item = mysql_fetch_array($results)) {\n    $item_title = $item['item_title'];\n    \n    // Check if the string contains Arabic characters\n    if (preg_match('/[\x{0600}-\x{06FF}\x{0750}-\x{077F}\x{08A0}-\x{08FF}]/u', $item_title)) {\n        echo "<h3>$item_title <em>(" . $Ar->ar2en($item_title) . ")</em></h3>";\n    } else {\n        echo "<h3>$item_title</h3>";\n    }\n}