<?php\n$reportServerUrl = "https://your-ssrs-server/reportserver";\n$reportPath = "/Path/To/Your/Report";\n$username = "your_username";\n$password = "your_password";\n\n$ch = curl_init();\ncurl_setopt($ch, CURLOPT_URL, $reportServerUrl . "?/ReportServer&/Pages/ReportViewer.aspx?%2f" . urlencode($reportPath));\ncurl_setopt($ch, CURLOPT_RETURNTRANSFER, 1);\ncurl_setopt($ch, CURLOPT_HTTPAUTH, CURLAUTH_BASIC);\ncurl_setopt($ch, CURLOPT_USERPWD, "$username:$password");\ncurl_setopt($ch, CURLOPT_SSL_VERIFYPEER, true); // Ensure SSL certificate is verified\n\n$response = curl_exec($ch);\nif(curl_errno($ch)) {\n    echo 'Curl error: ' . curl_error($ch);\n}\ncurl_close($ch);\n\necho $response;\n?>