use Cake\Validation\Validator;\n\n   class YourModelTable extends Table\n   {\n       public function validationDefault(Validator $validator): Validator\n       {\n           $validator\n               ->notEmptyString('field_name', __('שדה זה אינו יכול להישאר ריק'))\n               ->add('field_name', 'validFormat', [\n                   'rule' => 'email',\n                   'message' => __('כתובת האימייל אינה חוקית')\n               ]);\n\n           return $validator;\n       }\n   }