// Set the analyzer\n$analyzer = new Zend_Search_Lucene_Analysis_Analyzer_Common_Utf8();\nZend_Search_Lucene_Analysis_Analyzer::setDefault($analyzer);\n\n$queryString = 'name:sxample~';\n$query = Zend_Search_Lucene_Search_QueryParser::parse($queryString, 'utf-8');\n\ntry {\n    $hits = $index->find($query);\n} catch (Zend_Search_Lucene_Exception $ex) {\n    $hits = array();\n}\n\n// Output results\nforeach ($hits as $hit) {\n    echo 'Document ID: ', $hit->id, "\n";\n    echo 'Score: ', $hit->score, "\n";\n}