// Ensure mbstring is enabled\nif (!extension_loaded('mbstring')) {\n    die('The mbstring extension is not enabled.');\n}\n\n// Set internal encoding to UTF-8\nmb_internal_encoding('UTF-8');\n\n// Set encoding for multibyte regex functions\nmb_regex_encoding('UTF-8');\n\n// Example usage of mbstring functions\n$string = "こんにちは世界"; // "Hello World" in Japanese\necho "Length: " . mb_strlen($string, 'UTF-8') . "\n";\necho "Substring: " . mb_substr($string, 0, 5, 'UTF-8') . "\n";