// Assuming $response is the parsed XML response from the audioscrobbler API\n$recentTracks = $response->recenttracks->track;\n\n// Loop through the tracks to find the one currently playing\nforeach ($recentTracks as $track) {\n    // Check if the track is currently playing\n    $nowPlaying = (string) $track['nowplaying'];\n\n    if ($nowPlaying === 'true') {\n        // This track is currently playing\n        $artist = (string) $track->artist;\n        $songName = (string) $track->name;\n\n        echo "Currently playing: " . $artist . " - " . $songName;\n        break; // Exit the loop since we found the currently playing track\n    }\n}