function list_sub_categories_and_posts($parent_id = 3) {\n    $categories = get_categories(array('child_of' => $parent_id));\n    \n    foreach ($categories as $category) {\n        $postslist = get_posts(array(\n            'cat' => $category->term_id,\n            'numberposts' => 1,\n            'order' => 'DESC'\n        ));\n        \n        foreach ($postslist as $post) {\n            setup_postdata($post);\n            $option = '<li id="' . get_post_meta($post->ID, 'id', true) . '">';\n            $option .= '<a class="preview" rel="' . get_post_meta($post->ID, 'thumbnail', true) . '" ';\n            $option .= 'href="' . get_permalink($post->ID) . '">';\n            $option .= $category->name;\n            $option .= '</a>';\n            $option .= '</li>';\n            \n            echo $option;\n        }\n        wp_reset_postdata();\n    }\n}\n\n// Example usage\nlist_sub_categories_and_posts();