function bot_install() {\n    global $wpdb;\n    $table = $wpdb->prefix . "sh_gloss";\n\n    $structure = "CREATE TABLE $table (\n        id INT(9) NOT NULL AUTO_INCREMENT,\n        phrase VARCHAR(80) NOT NULL,\n        `desc` VARCHAR(255) NOT NULL,\n        UNIQUE KEY id (id)\n    );";\n\n    // Log SQL structure\n    error_log("Creating table with SQL: $structure");\n\n    $result = $wpdb->query($structure);\n\n    // Log result of table creation\n    if ($result === FALSE) {\n        error_log("Error creating table: " . $wpdb->last_error);\n    } else {\n        error_log("Table created successfully.");\n    }\n\n    // Populate table\n    $insert_sql = "INSERT INTO $table (phrase, `desc`)\n        VALUES ('Scott Herbert', 'Rockstar Programmer')";\n    \n    // Log SQL insert statement\n    error_log("Inserting test data with SQL: $insert_sql");\n\n    $result = $wpdb->query($insert_sql);\n\n    // Log result of data insertion\n    if ($result === FALSE) {\n        error_log("Error inserting data: " . $wpdb->last_error);\n    } else {\n        error_log("Data inserted successfully.");\n    }\n}