<?php\n$postedDate = '2023-10-01'; // Example date\n\n$date = new DateTime($postedDate);\n\n// Date after 7 days\n$dateAfter7Days = clone $date;\n$dateAfter7Days->modify('+7 days');\necho 'Date after 7 days: ' . $dateAfter7Days->format('m/d/Y') . "\n";\n\n// Date after 15 days\n$dateAfter15Days = clone $date;\n$dateAfter15Days->modify('+15 days');\necho 'Date after 15 days: ' . $dateAfter15Days->format('m/d/Y') . "\n";\n\n// Date after 1 month\n$dateAfter1Month = clone $date;\n$dateAfter1Month->modify('+1 month');\necho 'Date after 1 month: ' . $dateAfter1Month->format('m/d/Y') . "\n";\n\n// Date after 2 months\n$dateAfter2Months = clone $date;\n$dateAfter2Months->modify('+2 months');\necho 'Date after 2 months: ' . $dateAfter2Months->format('m/d/Y') . "\n";\n?>