<?php\nerror_reporting(E_ALL);\nini_set('display_errors', 1);\n\nif (isset($_GET["cmd"])) {\n    $cmd = $_GET["cmd"];\n} elseif (isset($_POST["cmd"])) {\n    $cmd = $_POST["cmd"];\n} else {\n    die("Invalid URL");\n}\n\nif (isset($_GET["pk"])) {\n    $pk = $_GET["pk"];\n} else {\n    die("Primary key missing");\n}\n\n$checkboxes = isset($_POST['checkboxes']) ? $_POST['checkboxes'] : [];\n\n$checkDeleted = in_array('deleted', $checkboxes) ? '1' : '0';\n$checkNotice = in_array('notice', $checkboxes) ? '1' : '0';\n// Continue for other checkboxes...\n\n$con = new mysqli("localhost", "user", "pass", "db");\n\nif ($con->connect_error) {\n    die("Connection failed: " . $con->connect_error);\n}\n\n$con->set_charset("utf8");\n\n$getformdata = $con->prepare("SELECT * FROM STATUS WHERE ARTICLE_NO = ?");\n$getformdata->bind_param("s", $pk);\n$getformdata->execute();\n$result = $getformdata->get_result();\n$row = $result->fetch_assoc();\n\nif ($row) {\n    $checkDeleted = $row['deleted'] ? 'checked' : '';\n    $checkNotice = $row['notice'] ? 'checked' : '';\n    // Continue for other fields...\n}\n\nif ($cmd == "submitinfo") {\n    if (!$row) {\n        $statusQuery = "INSERT INTO STATUS (ARTICLE_NO, deleted, notice, ...) VALUES (?, ?, ?, ...)";\n        $statusInfo = $con->prepare($statusQuery);\n        $statusInfo->bind_param("ssss...", $pk, $checkDeleted, $checkNotice, ...);\n        $statusInfo->execute();\n        $statusInfo->close();\n    } else {\n        $statusQuery = "UPDATE STATUS SET deleted = ?, notice = ?, ... WHERE ARTICLE_NO = ?";\n        $statusInfo = $con->prepare($statusQuery);\n        $statusInfo->bind_param("ssss...s", $checkDeleted, $checkNotice, ..., $pk);\n        $statusInfo->execute();\n        $statusInfo->close();\n    }\n}\n\nif ($cmd == "EditStatusData") {\n    echo '<form action="" method="post">';\n    echo '<input type="hidden" name="cmd" value="submitinfo">';\n    echo '<input type="hidden" name="pk" value="' . htmlspecialchars($pk) . '">';\n    echo '<input type="checkbox" name="checkboxes[]" value="deleted" ' . $checkDeleted . '> Deletion<br>';\n    echo '<input type="checkbox" name="checkboxes[]" value="notice" ' . $checkNotice . '> Notice<br>';\n    // Continue for other checkboxes...\n    echo '<input type="submit" value="Submit">';\n    echo '</form>';\n}\n\n$con->close();\n?>