<?php\n// IMAP connection details\n$hostname = '{imap.domain.com:993/imap/ssl}INBOX';\n$username = 'bounce_handle@domain.com';\n$password = 'yourpassword';\n\n// Open an IMAP stream to the mailbox\n$inbox = imap_open($hostname, $username, $password) or die('Cannot connect to Mailbox: ' . imap_last_error());\n\n// Search for all emails in the inbox\n$emails = imap_search($inbox, 'ALL');\n\nif ($emails) {\n    // Sort emails by date in descending order\n    rsort($emails);\n\n    foreach ($emails as $email_number) {\n        // Fetch the email header and body\n        $overview = imap_fetch_overview($inbox, $email_number, 0);\n        $message = imap_fetchbody($inbox, $email_number, 1.1);\n\n        if (empty($message)) {\n            $message = imap_fetchbody($inbox, $email_number, 1);\n        }\n\n        // Process the bounce email (e.g., analyze the content, log details, etc.)\n        echo "Subject: " . $overview[0]->subject . "\n";\n        echo "From: " . $overview[0]->from . "\n";\n        echo "Date: " . $overview[0]->date . "\n";\n        echo "Message: " . $message . "\n";\n        \n        // Mark the email as read\n        imap_setflag_full($inbox, $email_number, "\\Seen");\n    }\n}\n\n// Close the IMAP stream\nimap_close($inbox);\n?>