function deepCopyArray($array) {\n    $copy = [];\n    foreach ($array as $key => $value) {\n        if (is_array($value)) {\n            $copy[$key] = deepCopyArray($value);\n        } else {\n            $copy[$key] = $value;\n        }\n    }\n    return $copy;\n}\n\n// Usage:\n$results = [];\nforeach ($someLoop as $iteration) {\n    // Assuming $array is updated within the loop\n    $results[] = deepCopyArray($array);\n}