function getCustomHeader($headerName) {\n    $headerKey = 'HTTP_' . strtoupper(str_replace('-', '_', $headerName));\n    return isset($_SERVER[$headerKey]) ? $_SERVER[$headerKey] : null;\n}\n\n// Usage\n$batchType = getCustomHeader('BATCH_TYPE');\n$batchCount = getCustomHeader('BATCH_COUNT');\n$vendorId = getCustomHeader('VENDOR_ID');\n\necho "BATCH_TYPE: $batchType\n";\necho "BATCH_COUNT: $batchCount\n";\necho "VENDOR_ID: $vendorId\n";