$sanitized_post = array_map('sanitize_input', $_POST);\n\nfunction sanitize_input($data) {\n    // Remove extra spaces, newlines, etc.\n    $data = trim($data);\n    // Remove backslashes\n    $data = stripslashes($data);\n    // Convert special characters to HTML entities\n    $data = htmlspecialchars($data, ENT_QUOTES, 'UTF-8');\n    return $data;\n}\n\n// Now you can access elements of $sanitized_post as you would have of $_POST\n// Example:\n$sanitized_name = $sanitized_post['name'];\n$sanitized_email = $sanitized_post['email'];