<?php\n$entry = 'miami.com';\n$mysqli = new mysqli("localhost", "username", "password", "feather");\n\nif ($mysqli->connect_error) {\n    die("Connection failed: " . $mysqli->connect_error);\n}\n\n$result = $mysqli->query("SHOW TABLES FROM feather");\n\nif (!$result) {\n    die("Query failed: " . $mysqli->error);\n}\n\n$table_list = [];\n\nwhile ($row = $result->fetch_row()) {\n    $table = $row[0];\n    $stmt = $mysqli->prepare("SELECT COUNT(*) FROM `$table` WHERE `site` LIKE ?");\n    $stmt->bind_param('s', $entry);\n    $stmt->execute();\n    $stmt->bind_result($isThere);\n    $stmt->fetch();\n    $stmt->close();\n    \n    if ($isThere) {\n        $table_list[] = $table;\n    }\n}\n\n$mysqli->close();\n?>