$selectColumns = array(\n    'user_id.user_email',      // Select the user_email from user_id table\n    'user_details.first_name', // Select the first_name from user_details table\n    'user_details.last_name'   // Select the last_name from user_details table\n);\n\n$select = $handle->select()\n                 ->from(array('u' => 'user_id'), $selectColumns)\n                 ->join(array('ud' => 'user_details'), 'u.uid = ud.uid')\n                 ->where('u.uid = ?', $uid);\n\n$result = $select->query(Zend_Db::FETCH_OBJ);