<?php\n   // FTP connection details\n   $ftp_host = 'your.ftp.server';\n   $ftp_user = 'username';\n   $ftp_pass = 'password';\n\n   // Establish an FTP connection\n   $ftp_conn = ftp_connect($ftp_host);\n\n   // Login\n   if (!$ftp_conn || !ftp_login($ftp_conn, $ftp_user, $ftp_pass)) {\n       exit('Login Failed');\n   }\n\n   // Download a file\n   ftp_get($ftp_conn, '/local/path/file.txt', '/remote/path/file.txt', FTP_BINARY);\n\n   // Upload a file\n   ftp_put($ftp_conn, '/remote/path/file.txt', '/local/path/file.txt', FTP_BINARY);\n\n   // Close the connection\n   ftp_close($ftp_conn);\n   ?>