<?php\n   require 'vendor/autoload.php';\n\n   use phpseclib3\Net\SFTP;\n\n   // SFTP connection details\n   $sftp_host = 'your.sftp.server';\n   $sftp_port = 22; // Default SFTP port\n   $sftp_user = 'username';\n   $sftp_pass = 'password';\n\n   // Create an SFTP instance\n   $sftp = new SFTP($sftp_host, $sftp_port);\n\n   // Login\n   if (!$sftp->login($sftp_user, $sftp_pass)) {\n       exit('Login Failed');\n   }\n\n   // Download a file\n   $sftp->get('/remote/path/file.txt', '/local/path/file.txt');\n\n   // Upload a file\n   $sftp->put('/remote/path/file.txt', '/local/path/file.txt');\n\n   // Close the connection\n   $sftp->disconnect();\n   ?>