// In your CMS login script, include Flarum's authentication\nrequire 'path/to/flarum/bootstrap.php';\n\nuse Flarum\User\Login\UsernamePassword;\n\n// Assuming $cmsUser is the authenticated CMS user\n$actor = resolve('Flarum\User\Authenticator')->login(\n    new UsernamePassword($cmsUser['username'], $cmsUser['password'])\n);\n\n// Set Flarum session\nsession()->put('user', $actor->getId());