<?php\n\nclass SimpleClass {\n    private $property;\n\n    public function __construct($value) {\n        // Initialize properties\n        $this->property = $value;\n    }\n\n    public function doSomething() {\n        // Perform some actions\n        echo "Property value is: " . $this->property;\n    }\n}\n\n// Usage\n$instance = new SimpleClass("Hello, World!");\n$instance->doSomething(); // Explicitly call the method to perform the action\n?>