$mysqli = new mysqli("hostname", "username", "password", "database");\n\nif ($mysqli->connect_error) {\n    die("Connection failed: " . $mysqli->connect_error);\n}\n\n$stmt = $mysqli->prepare("INSERT INTO `$db_table` (username, live, datetime, ip, bump_count)\n                          VALUES (?, '1', ?, ?, 1)\n                          ON DUPLICATE KEY UPDATE\n                          live = VALUES(live),\n                          datetime = VALUES(datetime),\n                          ip = VALUES(ip),\n                          bump_count = bump_count + 1");\n\n$stmt->bind_param('ssss', $username, $timeofentry, $ip);\n\n$stmt->execute();\n$stmt->close();\n$mysqli->close();