if ($getFiles = $con->prepare($filesQuery)) {\n    $getFiles->bind_param("s", $pk);\n    $getFiles->execute();\n    $getFiles->bind_result($FILENAME);\n    $files = array();\n\n    while ($getFiles->fetch()) {\n        $filename = array(\n            'FILENAME' => $FILENAME,\n        );\n        $files[] = $filename;\n    }\n}\n\n$filesList = '';\nforeach ($files as $filenames) {\n    $filesList .= '<p><a href="./files/' . htmlspecialchars($filenames['FILENAME'], ENT_QUOTES, 'UTF-8') . '">' . htmlspecialchars($filenames['FILENAME'], ENT_QUOTES, 'UTF-8') . '</a></p>' . "\n"; \n}