// Assume $db2Connection is your DB2 connection\n$query = "SELECT SALES, ITEM FROM your_table";\n$result = db2_exec($db2Connection, $query);\n\nif ($result) {\n    // Initialize FusionCharts object\n    $FC = new FusionCharts("column2d", "exampleChart" , 600, 300, "chart-container", "json");\n\n    $data = array();\n    \n    while ($row = db2_fetch_assoc($result)) {\n        // Add data from DB2 result set\n        $FC->addData($row['SALES'], $row['ITEM']);\n    }\n\n    // Render the chart\n    $FC->render();\n} else {\n    echo "Error fetching data from DB2 database.";\n}