$pgConnection = pg_connect('host=127.0.0.1 dbname=test user=myuser password=xxxxx') or die('cant connect');\n\n$query = "INSERT INTO customer (name) VALUES ('blabla') RETURNING id"; // Assuming 'id' is the primary key column\n$result = pg_query($pgConnection, $query);\n\nif ($result) {\n    $last_id_results = pg_fetch_assoc($result);\n    print_r($last_id_results);\n} else {\n    echo "Error in insert query: " . pg_last_error($pgConnection);\n}\n\npg_close($pgConnection);