<?php\nrequire 'vendor/autoload.php';\nuse \Firebase\JWT\JWT;\n\n$key = "your_shared_secret_key";\n$payload = array(\n    "user_id" => $userId,\n    "email" => $userEmail,\n    "exp" => time() + 3600 // Token expires in 1 hour\n);\n\n$jwt = JWT::encode($payload, $key);\n\n// Set the JWT in a secure cookie\nsetcookie("auth_token", $jwt, time() + 3600, "/", "", true, true);\n\n// Redirect to ASP.NET application\nheader("Location: https://your-aspnet-app.com/welcome");\nexit();\n?>