// template.php\nclass Template {\n    protected $model;\n\n    public function __construct($model) {\n        $this->model = $model;\n    }\n\n    public function getModel() {\n        return $this->model;\n    }\n\n    public function special_include_parse($path, $model) {\n        if (file_exists($path)) {\n            $this->model = $model;\n            include $path;\n        } else {\n            throw new Exception("Template file not found: " . htmlspecialchars($path, ENT_QUOTES, 'UTF-8'));\n        }\n    }\n\n    public function Parse($model) {\n        $this->special_include_parse('/var/www/template.html.php', $model);\n    }\n}