<?php\n// Example migration script to transfer users from CMS to Flarum\nrequire 'path/to/your/cms/database/config.php';\nrequire 'path/to/flarum/bootstrap.php';\n\nuse Flarum\User\User;\n\n$db = new PDO($dsn, $username, $password);\n\n$query = $db->query("SELECT * FROM cms_users");\nwhile ($cmsUser = $query->fetch(PDO::FETCH_ASSOC)) {\n    $flarumUser = User::register(\n        $cmsUser['username'],\n        $cmsUser['email'],\n        $cmsUser['password'] // Ensure this is hashed appropriately\n    );\n    $flarumUser->save();\n}\n\necho "User migration completed.";