<?php\ntry {\n    // Create a new PDO instance\n    $pdo = new PDO('mysql:host=your_host;dbname=your_database', 'your_username', 'your_password');\n    $pdo->setAttribute(PDO::ATTR_ERRMODE, PDO::ERRMODE_EXCEPTION);\n    \n    // SELECT example\n    $stmt = $pdo->prepare('SELECT * FROM your_table WHERE id = :id');\n    $stmt->execute(['id' => $id]);\n    $result = $stmt->fetchAll(PDO::FETCH_ASSOC);\n    \n    // INSERT example\n    $stmt = $pdo->prepare('INSERT INTO your_table (column1, column2) VALUES (:value1, :value2)');\n    $stmt->execute(['value1' => $value1, 'value2' => $value2]);\n    \n    // UPDATE example\n    $stmt = $pdo->prepare('UPDATE your_table SET column1 = :value1 WHERE id = :id');\n    $stmt->execute(['value1' => $value1, 'id' => $id]);\n    \n    // DELETE example\n    $stmt = $pdo->prepare('DELETE FROM your_table WHERE id = :id');\n    $stmt->execute(['id' => $id]);\n\n} catch (PDOException $e) {\n    echo 'Connection failed: ' . $e->getMessage();\n}\n?>