function trim_html_text($text, $limit = 50) {\n    // Remove HTML tags\n    $plain_text = strip_tags($text);\n    \n    // Trim the plain text to the desired length\n    $trimmed_text = mb_substr($plain_text, 0, $limit);\n    \n    return $trimmed_text;\n}\n\n$html_content = "This is <b>my bold</b> text with more than 50 characters. Let's see how it handles trimming!";\n$trimmed_text = trim_html_text($html_content, 50);\n\necho $trimmed_text;