require 'vendor/autoload.php'; // Assuming you're using Composer\n\n// Step 1: Set up the Yahoo Client\n$yahoo = new Yahoo_Client('YOUR_YAHOO_CLIENT_ID', 'YOUR_YAHOO_CLIENT_SECRET', 'YOUR_REDIRECT_URI');\n\n// Step 2: Authenticate and get the OAuth2 token\nif (!isset($_GET['code'])) {\n    $authUrl = $yahoo->getAuthorizationUrl();\n    header('Location: ' . filter_var($authUrl, FILTER_SANITIZE_URL));\n} else {\n    $token = $yahoo->getAccessToken($_GET['code']);\n\n    // Step 3: Use the Yahoo API to get contacts\n    $contacts = $yahoo->getContacts($token);\n\n    foreach ($contacts as $contact) {\n        echo $contact['name'] . " - " . $contact['email'] . "<br>";\n    }\n}