function schoolDD($name, $selected = null) {\n    $select = '';\n    if ($selected != null) {\n        $select = $selected;\n    }\n\n    // Corrected SQL query\n    $qry = "SELECT * \n            FROM school\n            WHERE display = 'Y'\n            ORDER BY name, id";\n\n    // Assuming _execQry is a function that executes the query and returns an associative array of results\n    $schools = _execQry($qry);\n\n    $html = '<select name="' . htmlspecialchars($name, ENT_QUOTES, 'UTF-8') . '">';\n\n    foreach ($schools as $s) {\n        $html .= '<option value="' . htmlspecialchars($s['id'], ENT_QUOTES, 'UTF-8') . '"';\n        if ($select == $s['name']) {\n            $html .= ' selected="selected"';\n        }\n        $html .= '>' . htmlspecialchars($s['name'], ENT_QUOTES, 'UTF-8') . '</option>';\n    }\n    $html .= '</select>';\n    \n    return $html;\n}