<?php\nfunction hexToStr($hex) {\n    $str = '';\n    for ($i = 0; $i < strlen($hex); $i += 2) {\n        $str .= chr(hexdec(substr($hex, $i, 2)));\n    }\n    return $str;\n}\n\nfunction hashPassword($password) {\n    $encryptionKey = "AE09F72B007CAAB5";\n    $key = hexToStr($encryptionKey);\n    $hash = hash_hmac('sha1', $password, $key, true);\n    return base64_encode($hash);\n}\n\n// Example usage:\n$password = "pa55w0rd";\n$encodedPassword = hashPassword($password);\necho $encodedPassword; // Should output oK9NOVhpTkxLoLfvh1430SFb5gw=\n?>