<?php\n   // Get the chunk number and the total number of chunks\n   $chunk = isset($_POST['chunk']) ? intval($_POST['chunk']) : 0;\n   $chunks = isset($_POST['chunks']) ? intval($_POST['chunks']) : 0;\n   $fileName = isset($_POST['name']) ? $_POST['name'] : '';\n\n   // Create a temporary directory to store chunks\n   $tempDir = 'temp_uploads';\n   if (!is_dir($tempDir)) {\n       mkdir($tempDir, 0777, true);\n   }\n\n   // Save the chunk\n   $filePath = $tempDir . DIRECTORY_SEPARATOR . $fileName . '.part' . $chunk;\n   $out = fopen($filePath, 'wb');\n   $in = fopen($_FILES['file']['tmp_name'], 'rb');\n   while ($buff = fread($in, 4096)) {\n       fwrite($out, $buff);\n   }\n   fclose($in);\n   fclose($out);\n\n   // Check if all chunks are uploaded\n   if ($chunk == $chunks - 1) {\n       $finalPath = 'uploads' . DIRECTORY_SEPARATOR . $fileName;\n       $out = fopen($finalPath, 'wb');\n       for ($i = 0; $i < $chunks; $i++) {\n           $chunkPath = $tempDir . DIRECTORY_SEPARATOR . $fileName . '.part' . $i;\n           $in = fopen($chunkPath, 'rb');\n           while ($buff = fread($in, 4096)) {\n               fwrite($out, $buff);\n           }\n           fclose($in);\n           unlink($chunkPath);\n       }\n       fclose($out);\n   }\n\n   echo json_encode(['status' => 'success']);\n   ?>