<?php\nfunction fetchHTMLContent($url) {\n    $options = [\n        'http' => [\n            'method' => "GET",\n            'header' => "User-Agent: PHP\r\n"\n        ]\n    ];\n    $context = stream_context_create($options);\n    $html = file_get_contents($url, false, $context);\n    return $html;\n}\n\nfunction extractImageTags($html) {\n    $dom = new DOMDocument();\n    @$dom->loadHTML($html);\n    $imageTags = $dom->getElementsByTagName('img');\n    $images = [];\n    foreach ($imageTags as $tag) {\n        $src = $tag->getAttribute('src');\n        $width = $tag->getAttribute('width');\n        $height = $tag->getAttribute('height');\n        if ($src && filter_var($src, FILTER_VALIDATE_URL)) {\n            $images[] = [\n                'src' => $src,\n                'width' => $width,\n                'height' => $height\n            ];\n        }\n    }\n    return $images;\n}\n\nfunction filterImages($images, $minWidth = 100, $minHeight = 100) {\n    $filteredImages = [];\n    foreach ($images as $image) {\n        if ($image['width'] >= $minWidth && $image['height'] >= $minHeight) {\n            $filteredImages[] = $image;\n        }\n    }\n    return $filteredImages;\n}\n\nfunction createThumbnail($imageSrc, $thumbWidth = 100, $thumbHeight = 100) {\n    $image = imagecreatefromstring(file_get_contents($imageSrc));\n    $width = imagesx($image);\n    $height = imagesy($image);\n\n    $thumb = imagecreatetruecolor($thumbWidth, $thumbHeight);\n    imagecopyresized($thumb, $image, 0, 0, 0, 0, $thumbWidth, $thumbHeight, $width, $height);\n\n    ob_start();\n    imagejpeg($thumb);\n    $thumbnailData = ob_get_clean();\n\n    imagedestroy($image);\n    imagedestroy($thumb);\n\n    return $thumbnailData;\n}\n\n$url = "http://example.com";\n$html = fetchHTMLContent($url);\n$images = extractImageTags($html);\n$filteredImages = filterImages($images);\n\nforeach ($filteredImages as $image) {\n    $thumbnail = createThumbnail($image['src']);\n    // You can save the thumbnail or output it directly\n    file_put_contents('thumbnails/' . basename($image['src']), $thumbnail);\n}\n?>