require 'vendor/autoload.php'; // Assuming you're using Composer\n\n// Step 1: Set up the Google Client\n$client = new Google_Client();\n$client->setClientId('YOUR_GOOGLE_CLIENT_ID');\n$client->setClientSecret('YOUR_GOOGLE_CLIENT_SECRET');\n$client->setRedirectUri('YOUR_REDIRECT_URI');\n$client->addScope("https://www.googleapis.com/auth/contacts.readonly");\n\n// Step 2: Authenticate and get the OAuth2 token\nif (!isset($_GET['code'])) {\n    $authUrl = $client->createAuthUrl();\n    header('Location: ' . filter_var($authUrl, FILTER_SANITIZE_URL));\n} else {\n    $client->authenticate($_GET['code']);\n    $token = $client->getAccessToken();\n    $client->setAccessToken($token);\n\n    // Step 3: Use the People API to get contacts\n    $service = new Google_Service_PeopleService($client);\n    $connections = $service->people_connections->listPeopleConnections('people/me', array('personFields' => 'names,emailAddresses'));\n    \n    foreach ($connections->getConnections() as $person) {\n        $names = $person->getNames();\n        $emails = $person->getEmailAddresses();\n        echo $names[0]->getDisplayName() . " - " . $emails[0]->getValue() . "<br>";\n    }\n}