<?php\nfunction getRealPathFromUrl($url) {\n    // Assuming the URL is relative to the document root\n    $documentRoot = $_SERVER['DOCUMENT_ROOT'];\n    $parsedUrl = parse_url($url);\n    $path = $parsedUrl['path'];\n\n    // Resolve the real path\n    $realPath = realpath($documentRoot . $path);\n    return $realPath;\n}\n\nfunction getScriptContents($htmlContent) {\n    $dom = new DOMDocument();\n    libxml_use_internal_errors(true); // Suppress any parsing errors\n    $dom->loadHTML($htmlContent);\n    libxml_clear_errors();\n\n    $scriptContents = '';\n    $scripts = $dom->getElementsByTagName('script');\n\n    foreach ($scripts as $script) {\n        $src = $script->getAttribute('src');\n        if ($src) {\n            $realPath = getRealPathFromUrl($src);\n            if ($realPath && file_exists($realPath)) {\n                $scriptContents .= file_get_contents($realPath) . "\n";\n            }\n        }\n    }\n\n    return $scriptContents;\n}\n\n// Example usage\n$htmlContent = file_get_contents('path_to_your_html_file.html'); // Load your HTML content\n$allScriptsContent = getScriptContents($htmlContent);\n\n// Optionally, minify the JavaScript content using a minification library\n// For example, you could use a library like JSMin (https://github.com/rgrove/jsmin-php)\n// $minifiedScriptsContent = JSMin::minify($allScriptsContent);\n\n// Serve the combined JavaScript content\nheader('Content-Type: application/javascript');\necho $allScriptsContent;\n?>